contract_dict = {'cake':
                      {'MoonContract':'0xc812402a36a4e4d6331afa89bf92a89f8b6bd9c7',
                       'GateContract':'0x3789840c8697Aee0d02159003D7501588De3ceD9'},
                'pots-bnb':
                      {'MoonContract':'0x2f4a26A1B540255B5AA2c2e4A2d142ae2765b186',
                       'GateContract':'0xA9638f46b23FcfEEc9E000344D9b6ea7160b23Eb'}
                }

keys = dict.keys(contract_dict)

moon_chain_list = []
moon_name_list = []
moon_symbol_list = []
moon_balance_list = []
moon_decimal_list = []

for x in keys:
    moon = bsc_web3.eth.contract(address=Web3.toChecksumAddress(contract_dict[x]['MoonContract']), abi=moon_abi)
    gate = bsc_web3.eth.contract(address=Web3.toChecksumAddress(contract_dict[x]['GateContract']), abi=gate_abi)

    name = moon.functions.name().call()
    symbol = moon.functions.symbol().call()
    decimals = moon.functions.decimals().call()
    balance = gate.functions.userTotalBalance(wallet).call() / float(10**decimals)

    moon_chain_list.append('BSC')
    moon_name_list.append(name)
    moon_symbol_list.append(symbol)
    moon_decimal_list.append(decimals)
    moon_balance_list.append(balance)

moon_df = pd.DataFrame(list(zip(keys, moon_chain_list, moon_name_list, moon_symbol_list, moon_decimal_list, moon_balance_list)), columns=['Contract', 'Chain', 'Name', 'Symbol', 'Decimals', 'MoonTicket Qty'])
moon_df.to_csv(r'Z:\Python\DeFI\moonpot_positions.csv', index=False)
